<?php

namespace Instabox\Models\Label;

use Instabox\Models\Order\NestedModels\Recipient;
use Instabox\Models\Order\NestedModels\Sender;
use Instabox\Models\Order\Order;
use Picqer\Barcode\BarcodeGeneratorPNG;

class LabelHtmlGenerator
{
    protected Order $order;
    public function __construct(Order $order)
    {
        $this->order = $order;
    }


    public function getHtml(): string
    {
        $order = $this->order;

        $html = "
        <html>
            <head>";
        $html .= $this->getStyle();
        $html .= $this->addInstaboxLogo($this->getInstaboxLogo());
        $html .= $this->addDeliveryInformation($order->recipient, $order->sender);
        $html .= $this->addServiceType($order);
        $html .= $this->addDeliveryOption($order->delivery_option);
        $html .= $this->addBarcode($this->generateBarcodeImage());
        $html .= $this->addParcelId($order);
        $html .= $this->addInstaboxContactInformation();
        $html .= "
            </div>
            </div>
            </div>
            </body>
            </html>
        ";
        return $html;
    }

    protected function getStyle(): string
    {
        return "
            <style>
            body {
                font: 10px Arial, sans-serif;
            }
            header {
                padding-bottom: 20px;
            }
            p {
                margin:0;
                padding:0;
            }
            h3 {
                margin: 0;
                padding: 0;
                
            }
            .delivery-address {
                width: 50%;
                float: left;
            }
            .clearfix::after {
              content: '';
              clear: both;
              display: table;
            }
            .service-type-identifier {
                text-align: center;
                padding-top: 10px;
                font-size: 32px;
            }
            .sort-code {
                text-align: center;
                padding-top: 10px;
                font-size: 48px;
            }
            .barcode {
                width: 80%;
                float: left
            }
            .barcode-information {
                padding-top: 20px;
            }
            .parcel-identifier {
                padding-left: 10px;
            }
            .instabox-information {
                text-align: center;
                padding-top: 10px;
            }
        </style>
        ";
    }

    protected function addDeliveryInformation(Recipient $recipient, Sender $sender): string
    {
        return "
            <div class='delivery-information clearfix'>
            <div class='delivery-address'>
                <h3>Deliver to:</h3>
                <p>$recipient->name</p>
                <p>$recipient->mobile_phone_number</p>
                <p>$recipient->street</p>
                <p>Floor 1</p>
                <p>$recipient->zip $recipient->city, $recipient->country_code</p>
            </div>
            <div class='sender-address'>
                <h3>Sender:</h3>
                <p>$sender->name</p>
                <p>$sender->street</p>
                <p>$sender->zip $sender->city, $sender->country_code</p>
            </div>
        </div>
    ";
    }

    protected function generateBarcodeImage(): string
    {
        $generator = new BarcodeGeneratorPNG();
        return '<img height="100px" width="100%" src="data:image/png;base64,' . base64_encode($generator->getBarcode($this->order->parcel_id, $generator::TYPE_CODE_128)) . '">';
    }

    protected function getInstaboxLogo(): string
    {
        return "
            <img width='100%' src='data:image/png;base64,'/>        
        ";
    }

    /**
     * @param Order $order
     * @return string
     */
    protected function addServiceType(Order $order): string
    {
        return "
    <div class='service-type-identifier'>
        <p>$order->service_type</p>
    </div>";
    }

    /**
     * @param mixed $deliveryOption
     * @return string
     */
    protected function addDeliveryOption(mixed $deliveryOption): string
    {
        return "
    <div class='sort-code'>
        <p>$deliveryOption->sort_code</p>
    </div>";
    }

    /**
     * @param string $barcode
     * @return string
     */
    protected function addBarcode(string $barcode): string
    {
        return "
    <div class='barcode-information clearfix'>
        <div class='barcode'>
            $barcode
        </div>
        <div class='parcel-identifier'>
            <p>647</p>
        </div>
    </div>
    ";
    }

    /**
     * @param Order $order
     * @return string
     */
    protected function addParcelId(Order $order): string
    {
        return "
<div>
        <p>$order->parcel_id</p>
    </div>";
    }

    /**
     * @return string
     */
    protected function addInstaboxContactInformation(): string
    {
        return "
    <div class='instabox-contact'>
        <p>www.instabox.io</p>
    </div>";
    }

    /**
     * @param string $image
     * @return string
     */
    protected function addInstaboxLogo(string $image): string
    {
        return "
            </head>
            <body>
            <div class='clearfix'>
                    <header>
                        $image
                    </header>";
    }
}
